celllines.metadata <- as.data.frame(melt(celllines))
names(celllines.metadata) <- c("gsm", "cellline")

cellline.summary <- as.data.frame(unlist(lapply(celllines, function(x) length(x))))
names(cellline.summary) <- "sample_count"
cellline.summary$cellline <- rownames(cellline.summary)

celllines.metadata <- as.data.frame(merge(celllines.metadata, cellline.summary, by = "cellline", all.x = T))
gsm.duplicated <- unique(celllines.metadata$gsm[duplicated(celllines.metadata$gsm)])

celllines.metadata$duplicated <- ifelse(celllines.metadata$gsm %in% gsm.duplicated, 1, 0)

celllines.metadata1 <- celllines.metadata[which(celllines.metadata$duplicated == 1), ]
celllines.metadata0 <- celllines.metadata[which(celllines.metadata$duplicated == 0), ]

celllines.metadata1 <- celllines.metadata1 %>% 
  group_by(gsm) %>% 
  filter(sample_count == min(sample_count)) %>% 
  as.data.frame()
celllines.metadata1 <- distinct(celllines.metadata1, gsm, .keep_all= TRUE)

celllines.metadata <- as.data.frame(rbind(celllines.metadata0, celllines.metadata1))
celllines.metadata <- celllines.metadata[order(celllines.metadata$cellline), ]
